<script type="text/javascript">
    function showShf_new(data){
        if(data == -1 || data == -2 ) return;
        var ret = eval('('+data+')');
        var ifr_url = '';
        var show_1,show_2,show_3;
        if(ret.status == 40) show_1 = 5;
        if(ret.status == 42) show_1 = 6;
        if(ret.status2 == 40) show_2 = 5;
        if(ret.status3 == 40) show_3 = 5;
        if(ret.status2 == 42) show_2 = 6;
        if(ret.status3 == 42) show_3 = 6;
        var add_n = 0;
        /*相同活动参加多期只显示最新一期   
            同时有效的只有3期,以后只用三组来交替 show_3表示最新一期
        */
        var showDate = (new Date(2013,11,1)).getTime();//12.1
        var adDate = (new Date(2013,10,20,0)).getTime();//11.20
        var nowDate = (new Date()).getTime();
        if(nowDate < showDate) var split = 1; //第5期任务未开始

        if(show_1 == show_2) show_1 = 0;
        if(!split){
            if(show_2 == show_3) show_2 = 0;    
            if(show_1 == show_3) show_1 = 0;
        }else{
            if(show_1 && show_2 && show_3>1) show_3 = 0;//都参加的暂时不显示第5期
        }               
        if(show_1 || show_2 || show_3){
            if(show_1){
                ifr_url += '&show='+show_1+'&n='+ret.num+'&days='+ret.info;
            }
            if(show_2){
                ifr_url += '&show_2='+show_2+'&days_2='+ret.info2+'&n_2='+ret.num2;
            }
            if(show_3){
                ifr_url += '&show_3='+show_3+'&days_3='+ret.info3+'&n_3='+ret.num3;
            }
            if(ret.tel) ifr_url += '&tel='+ret.tel;
            ifr_url = ifr_url.replace('&','?');
            $.get("./dynmatch/ajax/shf.php"+ifr_url, {}, showShf_show);
            $("#shf_title_cp").css('display','block');
			$('#club_cp').attr('target','');
			$('#club_cp').attr('href','javascript:;');
        }                   
    }
    function showShf_show(data){
        var shf_div = jQuery('#shf_div');
        shf_div.html(data);
    }
</script>

