}
if(is_total == 0){
for(var nav_d in nav_domain){
if(refer.indexOf(nav_domain[nav_d]) != -1){
jy_head_function.click_pv('|jy_site_refer|'+nav_d);
is_total = 1;
refer_site = nav_d;
